package com.sparta.project.delivery.order;import com.sparta.project.delivery.common.BaseEntity;import com.sparta.project.delivery.menu.Menu;import jakarta.persistence.*;import lombok.*;@NoArgsConstructor(access = AccessLevel.PROTECTED)@AllArgsConstructor(access = AccessLevel.PRIVATE)@Builder@Getter@Entity@Table(name = "p_order_menu")public class OrderMenu extends BaseEntity {    @Id    @GeneratedValue(strategy = GenerationType.UUID)    @Column(name = "order_item_id")    private String orderItemId;    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "order_id", nullable = false)    private Order order;    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "menu_id", nullable = false)    private Menu menu;}