package com.sparta.project.delivery.region;import com.sparta.project.delivery.common.BaseEntity;import com.sparta.project.delivery.common.type.City;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.RequiredArgsConstructor;import lombok.Setter;@RequiredArgsConstructor@AllArgsConstructor@Getter@Entity@Table(name = "p_order")public class Region extends BaseEntity {    @Id    @GeneratedValue(strategy = GenerationType.UUID)    @Column(name = "region_id")    private String regionId;    @Setter    @Column(nullable = false)    @Enumerated(EnumType.STRING)    private City city;    @Setter    @Column(length = 10)    private String siGun;    @Setter    @Column(length = 10)    private String gu;    @Setter    @Column(length = 20)    private String village;}