package com.sparta.project.delivery.region.controller;import com.sparta.project.delivery.region.dto.RegionRequest;import com.sparta.project.delivery.region.dto.RegionResponse;import com.sparta.project.delivery.region.service.RegionService;import io.swagger.v3.oas.annotations.Operation;import io.swagger.v3.oas.annotations.tags.Tag;import lombok.RequiredArgsConstructor;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.data.domain.Sort;import org.springframework.data.web.PageableDefault;import org.springframework.web.bind.annotation.*;import java.util.Optional;@RequiredArgsConstructor@RestController@RequestMapping("/api/region")@Tag(name = "Region API", description = "지역을 추가 수정 삭제 조회 할 수 있는 API 입니다.")public class RegionController {    private final RegionService regionService;    @GetMapping    @Operation(summary = "region 조회 API", description = "region 정보를 조회합니다.")    public Page<RegionResponse> getRegion(            @PageableDefault(size = 10, sort = "city", direction = Sort.Direction.ASC) Pageable pageable    ) {        return regionService.getRegion(pageable).map(RegionResponse::from);    }    @PostMapping    public String addRegion(@RequestBody RegionRequest request) {        regionService.addRegion(request.toDto());        return "category create successfully";    }    @PutMapping("/{regionId}")    public Optional<RegionResponse> updateRegion(@PathVariable("regionId") String regionId, @RequestBody RegionRequest request) {        return regionService.updateRegion(regionId, request.toDto()).map(RegionResponse::from);    }    @DeleteMapping("/{regionId}")    public String deleteRegion(@PathVariable("regionId") String regionId) {        regionService.deleteRegion(regionId);        return "region delete successfully";    }}