package com.sparta.project.delivery.region.repository;import com.sparta.project.delivery.common.type.City;import com.sparta.project.delivery.region.entity.Region;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.data.jpa.repository.JpaRepository;import java.util.Optional;public interface RegionRepository extends JpaRepository<Region, String> {    Page<Region> findAllByIsDeletedFalse(Pageable pageable);    Optional<Region> findByCityAndSiGunAndGuAndVillageAndIsDeletedFalse(City city, String siGun, String gu, String village);    Optional<Region> findByRegionIdAndIsDeletedFalse(String regionId);}